import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';

const Map = () => {
  const [flights, setFlights] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFlights = async () => {
      try {
        const response = await fetch('https://opensky-network.org/api/states/all');
        if (!response.ok) {
          throw new Error('Network response was not ok ' + response.statusText);
        }
        const data = await response.json();
        if (data.states && Array.isArray(data.states)) {
          const flightsData = data.states
            .filter((state) => state[5] !== null && state[6] !== null) // Filter out flights with null lat/lng
            .map((state) => ({
              callsign: state[1],
              latitude: state[6],
              longitude: state[5],
              altitude: state[7],
            }));
          setFlights(flightsData);
        } else {
          throw new Error('Unexpected data format');
        }
      } catch (error) {
        console.error('Error fetching flight data:', error);
        setError(error.message);
      }
    };

    fetchFlights();
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <MapContainer center={[51.505, -0.09]} zoom={2} style={{ height: '100vh', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      {flights.map((flight, index) => (
        <Marker key={index} position={[flight.latitude, flight.longitude]}>
          <Popup>
            Flight: {flight.callsign} <br /> Altitude: {flight.altitude} ft
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default Map;
